import { Component, Input } from '@angular/core';
import { FormService } from '../services/form.service';


@Component({
  selector: 'app-form2',
  templateUrl: './form2.component.html',
  providers: [FormService]
})
export class Form2Component {
  selectedState = '';
  states: string[] = [];
  @Input selectedCountry: string = '';

  constructor(
    private formDataService: FormService,
  ) {}

  ngOnInit() {
    this.formDataService.selectedCountry$.subscribe((country) => {
      this.selectedCountry = country;
      this.states = this.formDataService.getStatesForCountry(country);   
    });
  }

  onStateChange(state: string) {
    this.selectedState = state;
    this.formDataService.setSelectedState(state);
  }

}
<form>
    <label for="state">Select State:</label>
    <select name="state" id="state" [(ngModel)]="selectedState" (change)="onStateChange(selectedState)">
      <option value="">-- Select State --</option>
      <ng-container *ngIf="states.length > 0; else noStates">
        <option *ngFor="let state of states" [value]="state">{{ state }}</option>
      </ng-container>
      <ng-template #noStates>
        <option disabled selected>-- No states available --</option>
      </ng-template>
    </select>
  </form> 

