import { Component, EventEmitter } from '@angular/core';
import { FormService } from '../Services/form.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-form1',
  templateUrl: './form1.component.html',
  providers: [FormService]
})
export class Form1Component {
  countries: string[] = [];
  selectedCountry = '';

  constructor(private formDataService: FormService, private router: Router) {}

  ngOnInit() {
    this.countries = this.formDataService.getCountries();
  }

  onCountryChange(event: Event) {
    const target = event.target as HTMLSelectElement;
    const country = target.value;
    this.formDataService.setSelectedCountry(country);
  }


}
<div class="form-wrapper">
    <form>
      <label for="country">Select Country:</label>
      <select name="country" id="country" [(ngModel)]="selectedCountry">
        <option value="">-- Select Country --</option>
        <option *ngFor="let country of countries" [value]="country">{{ country }}</option>
      </select>
    </form>
    </div>
